/////////////////////////////////////

import React from 'react';
import Categories from '../categories';
import { Col, Row } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';
import { Link } from 'react-router-dom'; // Corrected import
import './Home.css';

const categories = [
  { name: 'Electronics', img: 'path_to_image' },
  { name: 'Books', img: 'path_to_image' },
  { name: 'Clothing', img: 'path_to_image' },
];

function Home() {
  return (
    <div>
      <img
        src="https://res.cloudinary.com/learn-code-10/image/upload/v1653947013/yqajnhqf7usk56zkwqi5.png"
        className="home-banner"
        alt="Home Banner"
      />
      <div className="featured-products-container container mt-4">
        <h2>Last products</h2>
      </div>
      <div>
        <Link to="/category/all" style={{ textAlign: 'right', display: 'block', textDecoration: 'none' }}>
          See more {">>"}
        </Link>
      </div>

      <div className="sale_banner--container mt-4">
        <img
          src="https://res.cloudinary.com/learn-code-10/image/upload/v1654093280/xkia6f13xxlk5xvvb5ed.png"
          alt="Sale Banner"
        />
      </div>
      <div className="recent-products-container container mt-4">
        <h2>Categories</h2>
        <Row>
          {categories.map((category) => (
            <LinkContainer key={category.name} to={`/category/${category.name.toLowerCase()}`}>
              <Col md={4}>
                <div
                  style={{
                    backgroundImage: `linear-gradient(rgba(0,0,0,0.5), rgba(0,0,0,0.5)), url(${category.img})`,
                    gap: "10px"
                  }}
                  className="category"
                >
                  {category.name}
                </div>
              </Col>
            </LinkContainer>
          ))}
        </Row>
      </div>
    </div>
  );
}

export default Home;
