import { createApi, fetchBaseQuery } from "@reduxjs/toolkit/query/react";

// create the Api
const appApi = createApi({
  reducerPath: "appApi", // fixed typo
  baseQuery: fetchBaseQuery({ baseUrl: "http://localhost:3001" }),
  endpoints: (builder) => ({
    signup: builder.mutation({
      query: (user) => ({
        url: "/users/signup",
        method: "POST",
        body: user,
      }),
    }),
    login: builder.mutation({
      query: (user) => ({
        url: "/users/login",
        method: "POST",
        body: user,
      }),
    }),
  }),
});

export const { useSignupMutation, useLoginMutation } = appApi; // corrected the export

export default appApi;
